.loading-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  
  ion-spinner {
    margin-bottom: 16px;
  }
}

.stat-card {
  text-align: center;
  padding: 16px;
  
  .stat-icon {
    font-size: 48px;
    margin-bottom: 8px;
  }
  
  h2 {
    font-size: 32px;
    font-weight: bold;
    margin: 8px 0;
  }
  
  p {
    font-size: 14px;
    margin: 0;
    opacity: 0.8;
  }
}

ion-card {
  margin: 8px;
}

.chart-card {
  margin: 16px 0;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  
  ion-card-header {
    background: linear-gradient(135deg, #3880ff 0%, #5260ff 100%);
    color: #fff;
    padding: 12px 16px;
    
    ion-card-title {
      font-size: 16px;
      font-weight: 600;
      color: #fff;
    }
  }
}

.chart-content {
  padding: 16px !important;
  background-color: #ffffff;
  min-height: 280px;
}

.chart-wrapper {
  width: 100% !important;
  height: 250px !important;
  min-height: 250px !important;
  position: relative !important;
  display: block !important;
  background-color: #ffffff !important;
  
  canvas {
    display: block !important;
    width: 100% !important;
    height: 100% !important;
    min-height: 250px !important;
    background-color: #ffffff !important;
  }
}
