.loading-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 60vh;
  gap: 20px;
  
  p {
    color: var(--ion-color-medium);
  }
}

.empty-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 60vh;
  text-align: center;
  padding: 20px;
  
  ion-icon {
    font-size: 80px;
    color: var(--ion-color-medium);
    margin-bottom: 20px;
  }
  
  h2 {
    color: var(--ion-color-dark);
    margin: 10px 0;
  }
  
  p {
    color: var(--ion-color-medium);
    margin-bottom: 20px;
  }
}

.messages-container {
  padding: 16px;
  max-width: 1200px;
  margin: 0 auto;
}

.info-card {
  margin-bottom: 16px;
  
  ion-card-content {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 12px 16px;
    
    ion-icon {
      font-size: 24px;
      flex-shrink: 0;
    }
    
    p {
      margin: 0;
      font-size: 14px;
    }
  }
}

ion-card {
  margin-bottom: 12px;
}

ion-item {
  --padding-start: 16px;
  --inner-padding-end: 16px;
}

ion-label {
  h2 {
    font-weight: 600;
    color: var(--ion-color-dark);
    margin-bottom: 6px;
  }
  
  .last-message {
    color: var(--ion-color-medium);
    font-size: 0.9rem;
    margin: 4px 0;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
  }
  
  .message-time {
    display: flex;
    align-items: center;
    gap: 4px;
    font-size: 0.75rem;
    color: var(--ion-color-medium);
    margin-top: 4px;
    
    ion-icon {
      font-size: 14px;
    }
  }
}

.chat-details {
  display: flex;
  flex-direction: column;
  gap: 8px;
  padding-top: 8px;
  
  .detail-item {
    display: flex;
    align-items: center;
    gap: 8px;
    font-size: 0.85rem;
    color: var(--ion-color-dark);
    
    ion-icon {
      font-size: 16px;
    }
  }
}

ion-badge {
  font-size: 0.7rem;
  padding: 4px 10px;
}
