.loading-container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100%;
}

.empty-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  padding: 40px;
  text-align: center;

  ion-icon {
    font-size: 100px;
    color: var(--ion-color-success);
    margin-bottom: 20px;
  }
}

ion-list {
  padding: 16px;
}

ion-card {
  margin-bottom: 20px;

  img {
    width: 100%;
    height: 250px;
    object-fit: cover;
    cursor: pointer;
    transition: transform 0.2s;
    
    &:hover {
      transform: scale(1.02);
    }
  }

  .description {
    line-height: 1.5;
    margin-bottom: 15px;
  }

  .product-details {
    display: flex;
    flex-direction: column;
    gap: 8px;
    margin-bottom: 20px;
    padding: 15px;
    background: var(--ion-color-light);
    border-radius: 8px;

    .detail-item {
      display: flex;
      justify-content: space-between;
      
      strong {
        color: var(--ion-color-medium-shade);
      }
    }
  }

  .action-buttons {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 10px;
    margin-top: 15px;
  }
}

.image-modal {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.9);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 9999;
  
  .modal-content {
    position: relative;
    max-width: 90%;
    max-height: 90%;
    
    img {
      width: 100%;
      height: auto;
      max-height: 90vh;
      object-fit: contain;
    }
    
    .close-button {
      position: absolute;
      top: 10px;
      right: 10px;
      --background: rgba(0, 0, 0, 0.5);
      z-index: 10000;
    }
  }
}
