commits.content:
.report-container {
  padding: 20px;
  max-width: 1400px;
  margin: 0 auto;
}

.loading-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 50vh;
  gap: 20px;
}

.stat-card {
  ion-card-content {
    text-align: center;
    padding: 20px;
    
    ion-icon {
      font-size: 40px;
      margin-bottom: 10px;
    }
    
    h2 {
      margin: 10px 0 5px 0;
      font-size: 24px;
      font-weight: bold;
      color: var(--ion-color-dark);
    }
    
    p {
      margin: 0;
      font-size: 14px;
      color: var(--ion-color-medium);
    }
  }
}

.chart-container {
  height: 300px;
  position: relative;
  
  canvas {
    max-height: 300px;
  }
}

@media print {
  ion-header,
  ion-button,
  .no-print {
    display: none !important;
  }
  
  .report-container {
    padding: 10px;
  }
  
  ion-card {
    page-break-inside: avoid;
  }
}

ion-card {
  margin-bottom: 20px;
}

ion-list ion-item {
  --padding-start: 16px;
  --inner-padding-end: 16px;
}
