.loading-container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100%;
  padding: 2rem;
}

.empty-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  padding: 2rem;
  text-align: center;
  color: var(--ion-color-medium);

  ion-icon {
    font-size: 80px;
    margin-bottom: 1rem;
  }

  h2 {
    margin: 0.5rem 0;
    color: var(--ion-color-dark);
  }

  p {
    margin: 0.5rem 0;
  }
}

.avatar-placeholder {
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, var(--ion-color-primary-tint), var(--ion-color-secondary-tint));
  border-radius: 50%;

  ion-icon {
    color: white;
    font-size: 24px;
  }
}

ion-avatar {
  width: 48px;
  height: 48px;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
}
