commits.content:
.loading-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  gap: 15px;

  p {
    color: var(--ion-color-medium);
  }
}

.empty-chat {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  padding: 20px;
  text-align: center;

  ion-icon {
    font-size: 80px;
    color: var(--ion-color-medium);
    margin-bottom: 20px;
  }

  p {
    color: var(--ion-color-medium);
    font-size: 16px;
  }
}

.chat-messages {
  padding: 20px;
  overflow-y: auto;
  height: 100%;

  .message {
    margin-bottom: 15px;
    display: flex;

    &.message-sent {
      justify-content: flex-end;

      .message-content {
        background: var(--ion-color-primary);
        color: white;
        border-radius: 18px 18px 4px 18px;
      }
    }

    &.message-received {
      justify-content: flex-start;

      .message-content {
        background: var(--ion-color-light);
        color: var(--ion-color-dark);
        border-radius: 18px 18px 18px 4px;
      }
    }

    .message-content {
      max-width: 70%;
      padding: 12px 16px;
      box-shadow: 0 1px 2px rgba(0, 0, 0, 0.1);

      .message-sender {
        font-size: 12px;
        font-weight: bold;
        margin: 0 0 5px 0;
        opacity: 0.8;
      }

      .message-text {
        margin: 0;
        font-size: 15px;
        word-wrap: break-word;
      }

      .message-time {
        font-size: 11px;
        margin: 5px 0 0 0;
        opacity: 0.7;
      }
    }
  }
}

ion-footer {
  ion-toolbar {
    --padding-start: 0;
    --padding-end: 0;

    ion-item {
      --padding-start: 8px;
      --padding-end: 8px;
      --inner-padding-end: 0;

      ion-input {
        --padding-start: 12px;
        --padding-end: 12px;
      }

      ion-button {
        margin: 0;
      }
    }
  }
}
