commits.content:
.loading-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  
  ion-spinner {
    margin-bottom: 16px;
  }
}

.empty-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  padding: 32px;
  text-align: center;
  
  ion-icon {
    font-size: 96px;
    color: var(--ion-color-medium);
    margin-bottom: 16px;
  }
  
  h2 {
    color: var(--ion-color-dark);
    margin-bottom: 8px;
  }
  
  p {
    color: var(--ion-color-medium);
    margin-bottom: 20px;
  }

  ion-button {
    max-width: 300px;
  }
}

.chat-messages {
  padding: 16px;
  height: calc(100% - 60px);
  overflow-y: auto;
}

.message {
  margin-bottom: 16px;
  display: flex;
  
  &.message-sent {
    justify-content: flex-end;
    
    .message-content {
      background: var(--ion-color-primary);
      color: white;
      border-radius: 16px 16px 4px 16px;
    }
  }
  
  &.message-received {
    justify-content: flex-start;
    
    .message-content {
      background: var(--ion-color-light);
      color: var(--ion-color-dark);
      border-radius: 16px 16px 16px 4px;
    }
  }
}

.message-content {
  max-width: 70%;
  padding: 12px 16px;
  
  .message-sender {
    font-size: 12px;
    font-weight: bold;
    margin: 0 0 4px 0;
    opacity: 0.8;
  }
  
  .message-text {
    margin: 0;
    word-wrap: break-word;
  }
  
  .message-time {
    font-size: 10px;
    margin: 4px 0 0 0;
    opacity: 0.7;
  }
}

ion-footer {
  ion-toolbar {
    --background: var(--ion-color-light);
    --padding-start: 8px;
    --padding-end: 8px;
  }
  
  ion-item {
    --background: transparent;
  }
}
