.profile-container {
  padding: 16px;
  max-width: 800px;
  margin: 0 auto;
}

.profile-header {
  text-align: center;
  padding: 2rem 1rem;

  .large-avatar {
    width: 120px;
    height: 120px;
    margin: 0 auto 1rem;

    img {
      width: 100%;
      height: 100%;
      object-fit: cover;
    }
  }

  .avatar-placeholder {
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    background: linear-gradient(135deg, var(--ion-color-primary-tint), var(--ion-color-secondary-tint));
    border-radius: 50%;

    ion-icon {
      color: white;
      font-size: 48px;
    }
  }

  h2 {
    margin: 0.5rem 0;
    font-size: 1.8rem;
    font-weight: 700;
  }

  p {
    margin: 0.25rem 0;
    color: var(--ion-color-medium);
  }
}

.edit-actions {
  margin-top: 1.5rem;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}
