.dashboard-container {
  max-width: 1200px;
  margin: 0 auto;

  h1 {
    font-size: 28px;
    margin-bottom: 5px;
  }

  .subtitle {
    color: var(--ion-color-medium);
    margin-bottom: 30px;
  }

  ion-card {
    cursor: pointer;
    transition: transform 0.2s;

    &:hover {
      transform: translateY(-5px);
    }

    ion-card-content {
      text-align: center;
      padding: 30px 20px;

      &.card-with-badge {
        position: relative;
      }

      .big-icon {
        font-size: 48px;
        margin-bottom: 15px;
      }

      .notification-badge {
        position: absolute;
        top: 10px;
        right: 10px;
        font-size: 11px;
        font-weight: bold;
        min-width: 20px;
        height: 20px;
        padding: 0 6px;
        border-radius: 10px;
        display: flex;
        align-items: center;
        justify-content: center;
      }

      h2 {
        font-size: 18px;
        margin: 10px 0;
      }

      p {
        color: var(--ion-color-medium);
        font-size: 14px;
        margin: 0;
      }
    }
  }
}
