 ion-thumbnail {
   --size: 60px;
   --border-radius: 8px;
+}
+
+.payment-proof-section {
+  margin-top: 20px;
+  padding-top: 20px;
+  border-top: 1px solid var(--ion-color-light);
+
+  h3 {
+    margin-bottom: 12px;
+    font-size: 1rem;
+    font-weight: 600;
+  }
+
+  .proof-image-container {
+    border-radius: 8px;
+    overflow: hidden;
+    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
+    margin-bottom: 16px;
+    cursor: pointer;
+
+    img {
+      width: 100%;
+      max-height: 300px;
+      object-fit: contain;
+      display: block;
+      background: var(--ion-color-light);
+    }
+  }
+
+  .verification-actions {
+    display: flex;
+    flex-direction: column;
+    gap: 12px;
+    margin-top: 16px;
+  }
+
+  .verification-note {
+    display: flex;
+    align-items: center;
+    gap: 8px;
+    margin-top: 12px;
+    padding: 12px;
+    background: var(--ion-color-success-tint);
+    border-radius: 8px;
+
+    ion-icon {
+      font-size: 24px;
+    }
+  }
+}
+
+.payment-note {
+  display: block;
+  margin-top: 12px;
+  padding: 12px;
+  background: var(--ion-color-light);
+  border-radius: 8px;
+  text-align: center;
 }
