.report-container {
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
}

.loading-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 50vh;
  gap: 20px;
}

.stat-card {
  ion-card-content {
    text-align: center;
    padding: 20px;
    
    ion-icon {
      font-size: 40px;
      margin-bottom: 10px;
    }
    
    h2 {
      margin: 10px 0 5px 0;
      font-size: 24px;
      font-weight: bold;
    }
    
    p {
      margin: 0;
      font-size: 14px;
      color: var(--ion-color-medium);
    }
  }
}

.chart-container {
  height: 300px;
  position: relative;
}
